import type * as Stitches from '../index.js'
import { createStitches } from '../index.js'

const { css, styled } = createStitches()

type ComponentProps = { css?: Stitches.CSS }

// test react component

const Box = styled('div', { color: 'CurrentColor' })

export function ReactComponent(props: ComponentProps) {
	return <Box css={props.css} />;
}

export function WrappedReact() {
	return <ReactComponent css={{
		/* suggestions */
		color: 'ActiveText',
		display: 'block',
	}} />;
}

// test core component

const box = css({ color: 'ActiveText' })

export function CoreComponent(props: ComponentProps) {
	return <div className={box({ css: props.css })} />;
}

export function WrappedCore() {
	return <CoreComponent css={{
		/* suggestions */
		color: 'ActiveText',
	}} />;
}
