import * as Stitches from '@stitches/react'

const { config, css, theme } = Stitches.createStitches({
	theme: {
		color: {
			current: 'CurrentColor',
		},
	},
	utils: {
		/** The **mx** property is so cool. */
		marginLeft: (value: number) => ({
			marginLeft: value,
			marginRight: value,
		})
	}
})

css({
	marginLeft: 50,
})
