import * as Stitches from '@stitches/react'

const { config, css, theme } = Stitches.createStitches({
	theme: {
		color: {
			current: 'CurrentColor',
		},
	},
	utils: {
		/** The **mx** is soo cool. */
		mx: (value: number) => ({
			marginLeft: value,
			marginRight: value,
		})
	}
})

const shared: Stitches.CSS<typeof config> = {
	color: theme.color.current,
	mx: 50,
}

const component = css(shared)
