import * as Stitches from '@stitches/react'

type MyComponentProps = { css?: Stitches.CSS }

const Component = Stitches.styled('a')

function MyComponent(props: React.ComponentProps<typeof Component>) {
	<Component css={props.css} />
}

export function Test() {
	return <MyComponent css={{ color: 'ActiveText' }} />
}
