import type * as Stitches from '../index.js'
import { createStitches } from '../index.js'

const { styled } = createStitches({
	theme: {
		colors: {
			red: 'Crimson',
		},
	},
	utils: {
		mx: (value: Stitches.PropertyValue<'marginLeft'>) => ({
			marginLeft: value,
			marginRight: value,
		})
	}
})

const Styled = styled('div', {
	display: 'block',
	marginLeft: 5,
	mx: 5,
})
