import React from 'react'
import * as Stitches from '../index.js'

const { styled } = Stitches.createStitches({
	media: {
		bp1: '(min-width: 480px)',
		bp2: '(min-width: 720px)',
	}
})

const Styled = styled('div', {
	variants: {
		variant: {
			pink: {},
			blue: {},
		}
	},
})

export function StyledPage() {
	<Styled
		variant={{
			"@initial": "blue",
			"@bp2": "pink"
		}}
	/>
}
