import * as Stitches from '../index';

const { styled, theme } = Stitches.createStitches({
	theme: {
		space: {
			1: '15px',
			2: '30px',
			3: '60px',
		}
	},
	utils: {
		mx: (value: Stitches.ScaleValue<'space'>) => ({
			marginLeft: value,
			marginRight: value,
		}),
	},
})

theme.space[1].scale

const Box = styled('a', {
	color: 'ActiveText',
	mx: theme.space[1],
	'@media all': {
		color: 'ActiveText',
	},
	variants: {
		size: {
			sm: {},
			lg: {},
		}
	}
})

export function Example() {
	return <Box size={{
		'@initial': 'lg',
	}} css={{
		color: 'ActiveText',
	}} />
}
