import type * as Stitches from '../index.js'
import { createStitches } from '../index.js'

const { styled } = createStitches({
	media: {
		bp1: '(min-width: 480px)',
		bp2: '(min-width: 960px)',
	},
})

const Component = styled('div', {
	variants: {
		size: {
			1: {
				size: '15px',
			},
			2: {
				size: '30px',
			},
		},
	},
})

export function Example1() {
	return (
		<Component size="1" />
	)
}

export function Example2() {
	return (
		<Component size={{
			"@initial": "1",
			"@media (min-width: 640px)": "2",
		}} />
	)
}
