import type * as Stitches from '../index.js'
import { createStitches } from '../index.js'

const { styled } = createStitches({
	theme: {
		space: {
			1: "5px",
			2: "10px",
			3: "15px",
			4: "20px",
			5: "25px",
			6: "35px",
		},
	},
	utils: {
		/** The **mx** property sets the margin area on the horizontal sides of an element. */
		mx: (value: Stitches.ScaleValue<'space'>) => ({
			marginLeft: value,
			marginRight: value,
		}),
	},
})

const Box = styled('a', {})

export default function App() {
	return <Box css={{
		// changing "$1" to 50 should fail
		mx: '$1'
	}} href="" />
}
